import React, { useEffect, useState, Route } from "react";
// import "../stylesheets/MovieCard.css";
import Acard from './Acard';
import axios from "axios";


const Card = (props) => {
  const [cards, setCards] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      await axios
        .get("http://localhost:5000/getdata")
        .then(({ data: foundMovies }) => {
          console.info(foundMovies);
          console
          setCards(foundMovies);
        })
        .catch((error) => {
          console.error("Some error", error);
        });
    };
    fetchMovies();
  }, []);

  const renderCards = cards.map((card) => <Acard card={card} />);

  return (
    <div className={"section"}>
      <h1 style={{ padding: "5vh 5%" }}>
        {<Route path={"/movies"} /> ? String("MOVIES:") : String("MOVIES")}
      </h1>
      <div class="ui three column grid">{renderCards}</div>
    </div>
  );
};
//dsadsa

export default Card;
