import React , { useEffect, useState }from "react";
const Timeline = () => {
    const [userData, setUserData] = useState({name:"", email:"", phone:"", message:""});

    const userContact = async () => {
        try {
            const res = await fetch('/getdata', {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                },
            });

            const data = await res.json();
            console.log(data);
            setUserData({...userData, name:data.name, email:data.email, phone:data.phone });

            if (!res.status === 200) {
                const error = new Error(res.error);
                throw error;
            }

        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {
        userContact();
    }, []);

    // we are storing data in states 

    const handleInputs = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setUserData({ ...userData, [name]:value });
    } 



 const cardInfo=[
       {orgname:udata.name,jobtitle:userData.jobtitle,salary:userData.salary,location:userData.location}
    ];

const renderCard = (card , index) =>{
    return(<div className="timeline_info_item d-flex flex-row align-items-center justify-content-start" key={index}>
                        <div className="contact_info_content">
                            <div className="contact_info_title">{card.jobtitle}</div>
                            <div className="contact_info_text">{card.orgname}</div>
                            <div className="contact_info_text">{card.location}</div>
                            <div className="contact_info_text">{card.salary}</div>
                        </div>
                    </div> 
)
}


    return (
    <>


  <div className="contact_info">
     <div className="container-fluid">
         <div className="row">
             <div className="col-lg-10 offset-lg-1">
                 <div className="contact_info_container d-flex flex-lg-row flex-column justify-content-between align-items-between">
                     {cardInfo.map(renderCard)}

                     </div>
             </div>
             <div className="col-lg-10 offset-lg-1">
                 <div className="contact_info_container d-flex flex-lg-row flex-column justify-content-between align-items-between">
                     {cardInfo.map(renderCard)}

                     </div>
             </div>
             <div className="col-lg-10 offset-lg-1">
                 <div className="contact_info_container d-flex flex-lg-row flex-column justify-content-between align-items-between">
                     {cardInfo.map(renderCard)}

                     </div>
             </div>

         </div>

     </div>

     </div>

    </>
  );
};

export default Timeline;
